$(document).ready(function () {
var c = $(".hidden");
var d = $(".visible");
var pos = c.position();
$('#wrapwrap').scroll(function () {
var windowpos = $('#wrapwrap').scrollTop();
if (windowpos >= pos.top & windowpos >= 100) {
c.addClass("hide");
d.addClass("showd");
} else {
c.removeClass("hide");
d.removeClass("showd");
}
});
});

